mapper.select:
select * from `tb_purchase`
where #{imageOwner}=image_owner
 and state=#{state} 
 and isClosed = 0
ORDER BY end_time DESC
limit #{begin}, #{count}

select * from `tb_purchase`
where #{purchaser}=purchaser
 and state=#{state} 
 and isClosed = 0
ORDER BY end_time
limit #{begin}, #{count}
